import React, {Component} from 'react';
import monkey from './monkey.png';
import styled, { keyframes } from "styled-components";
import { flip } from 'react-animations';

const FlipAnimation = keyframes`${flip}`;
const FlipDiv = styled.div`
  animation: infinite 2s ${FlipAnimation};
  top: 35%;
  left: 40%;
  display: flex;
  justify-content: center;
  position: absolute;
`;


class App extends Component {
    state = { selectedFile: null};


	onFileChange = (event) => {
		this.setState({selectedFile: event.target.files[0]});
		console.log(event)

	};	

	checker = () => {
		if (this.state.selectedFile){
    	return(
		<div>
    		<FlipDiv> <img src={monkey} /> </FlipDiv>
			<label style={{top:"50% !important", position: "absolute", display:"flex", margin: "-170px 0 0 20px"}}> LOADING </label>
		</div>
    	);}
    	else{
    	return(
    	<div>
        	<img src={monkey} />
        	<label> UPLOAD PNG 
            	<input type="file" name="file" onChange={this.onFileChange} />
        	</label>
    	</div>
    	);}
}	

    render() {
		console.log(this.state.selectedFile)
      return (
		<div>
        	<h> GRAPHMONKEY</h>
			{this.checker()}
		</div>
     );
   }
 }

export default App;
