import React, {Component} from 'react';
import monkey from './monkey.png';
import styled, { keyframes } from "styled-components";
import { flip } from 'react-animations';

const FlipAnimation = keyframes`${flip}`;
const FlipDiv = styled.div`
  animation: infinite 2s ${FlipAnimation};
`;


class Image extends Component {
	render(){
	if (this.props.data.selectedFile){
	return(
	<FlipDiv> <img src={monkey} /> </FlipDiv>
	);}
	else{
	return(
	<div>
		<img src={monkey} />
    	<label> UPLOAD PNG 
    		<input type="file"  Change={this.onFileChange} />
    	</label>
	</div>
	);}
	}
}

class App extends Component {
    state = { selectedFile: null};


	onFileChange = (event) => {
		this.setState({selectedFile: event.target.files[0]});
		console.log(event)

	};	

	checker = () => {
		if (this.state.selectedFile){
    	return(
    	<FlipDiv> <img src={monkey} /> </FlipDiv>
    	);}
    	else{
    	return(
    	<div>
        	<img src={monkey} />
        	<label> UPLOAD PNG 
            	<input type="file" name="file" Change={this.onFileChange} />
        	</label>
    	</div>
    	);}
}	

    render() {
		console.log(this.state.selectedFile)
      return (
		<div>
        	<h> GRAPHMONKEY</h>
			{this.checker()}
		</div>
     );
   }
 }

export default App;
