import React, {Component} from 'react';
import monkey from './monkey.png';
import styled, { keyframes } from "styled-components";
import { flip } from 'react-animations';
import axios from 'axios';

const FlipAnimation = keyframes`${flip}`;
const FlipDiv = styled.div`
  animation: infinite 2s ${FlipAnimation};
  top: 35%;
  left: 40%;
  display: flex;
  justify-content: center;
  position: absolute;
`;


class App extends Component {
    state = { selectedFile: null,
				image1: null,
				image2: null,
				image3: null};


	onFileChange = (event) => {
		const file = event.target.files[0]
		this.setState({selectedFile: file});
		console.log(event)
		console.log(this.state)

		let data = new FormData();
		data.append('graph_image', file);

		axios.post('http://ec2-3-101-117-97.us-west-1.compute.amazonaws.com:3000',
  			data)
		.then((response) => {
			console.log("Response");
  			console.log(response);
			//console.log(response.data.form);
			//console.log(response.data.headers);
			this.setState({image1: new Buffer.from(response.data.image1).toString("base64"),
						image2: new Buffer.from(response.data.image2).toString("base64"),
						image3: new Buffer.from(response.data.image3).toString("base64")})
		}, (error) => {
  		console.log(error);
		});

}	

	checker = () => {
		if (this.state.selectedFile){
    	return(
		<div>
    		<FlipDiv> <img src={monkey} /> </FlipDiv>
			<label style={{top:"50% !important", position: "absolute", display:"flex", margin: "-170px 0 0 20px"}}> LOADING </label>
		</div>
    	);}
    	else{
    	return(
    	<div>
        	<img src={monkey} />
        	<label> UPLOAD PNG 
            	<input type="file" name="file" onChange={this.onFileChange} />
        	</label>
    	</div>
    	);}
}


	checker2 = () => {
		if (this.state.image1 && this.state.image2 && this.state.image3){return (<div><img src={'data:image/png;base64,' + this.state.image1}/></div>)}

}


    render() {
		console.log(this.state.selectedFile)
      return (
		<div>
        	<h> GRAPHMONKEY</h>
			{this.checker()}
			{this.checker2()}
		</div>
     );
   }
 }

export default App;
